import React, { useState, useEffect } from 'react';

const App = () => {
  const [quotes, setQuotes] = useState([]);
  const [filterText, setFilterText] = useState('');
  const [selectedCharacter, setSelectedCharacter] = useState('Todos');

  useEffect(() => {
    fetch('https://beta.adalab.es/curso-intensivo-fullstack-recursos/apis/quotes-friends-tv-v1/quotes.json')
      .then(response => response.json())
      .then(data => setQuotes(data.quotes));
  }, []);

  const handleFilterTextChange = event => {
    setFilterText(event.target.value);
  };

  const handleCharacterChange = event => {
    setSelectedCharacter(event.target.value);
  };

  const filteredQuotes = quotes.filter(quote => {
    const matchText = quote.phrase && quote.phrase.toLowerCase().includes(filterText.toLowerCase());
    const matchCharacter =
      selectedCharacter === 'Todos' || (quote.character && quote.character === selectedCharacter);
    return matchText && matchCharacter;
  });

  return (
    <div>
      <h1>Frases de Friends</h1>

      <div>
        <input
          type="text"
          value={filterText}
          onChange={handleFilterTextChange}
          placeholder="Filtrar por frase"
        />

        <select value={selectedCharacter} onChange={handleCharacterChange}>
          <option value="Todos">Todos</option>
          <option value="Ross">Ross</option>
          <option value="Monica">Monica</option>
          <option value="Joey">Joey</option>
          <option value="Phoebe">Phoebe</option>
          <option value="Chandler">Chandler</option>
          <option value="Rachel">Rachel</option>
        </select>
      </div>

      <ul>
        {filteredQuotes.map((quote, index) => (
          <li key={index}>
            <strong>{quote.character}: </strong>
            {quote.phrase}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
